mapper.select:
select APID, APPID, APSID, APCID, APSUBMITTIME, APSTATUS
from ks.applyparttimejob
where  APSTATUS =10 or APSTATUS =30

   and APID=#{apId}


and APPID=#{apPid}


and APSID=#{apSid}


and APCID=#{apCid}

mapper.update:
update ks.applyparttimejob
set APSTATUS = #{apStatus}


        APID =#{apId}


        and APPID=#{apPid}


        and APSID =#{apSid}


        and APCID=#{apCid}
