mapper.select:
select FFILEID, FFILENAME, FFILESTOREPATH, FFILECID, FFILEFLAG
from ks.filecompany
where FFILEFLAG=0
and FFILEID=#{fFileID}
and FFILENAME=#{fFileName}
 and FFILESTOREPATH=#{fFileStorePath}
 and FFILECID=#{fFileCid}
 and FFILEFLAG=#{fFileFlag}

mapper.update:
update ks.filecompany

     FFILENAME = #{fFileName},
      FFILESTOREPATH=#{fFileStorePath},
      FFILEFLAG=#{fFileFlag},


    FFILEID=#{fFileID}
     and FFILECID=#{fFileCid}

mapper.select:
select FFILEID, FFILENAME, FFILESTOREPATH, FFILECID
from ks.filecompany
where FFILECID=#{fFileCid}
